import 'react-json-pretty/themes/monikai.css';


var JSONPrettyMon = require('react-json-pretty/dist/monikai');

class Item extends React.Component {
    constructor(props) {
        super(props);
    }
    render(){
        return ( 
        <div>
        <h1> Random Recipe of Today: </h1>
        </div>
      )}
}



export default class Home extends React.Component {

    state = {
        json_response: []
    };


    componentDidMount() {
        const url = "https://jz9ap7x9if.execute-api.us-east-1.amazonaws.com/dev/getmessage";

        axios.get(url,{
            headers: { 'Access-Control-Allow-Origin': '*',"Access-Control-Allow-Credentials": true}
        }).then(res => {
            console.log(JSON.stringify(res.data));
            this.setState({                json_response: res.data          });

            



        }).catch(err => {
            this.setState({
                json_response: null
            })
        });
   
    }

    
    renderlist() {
        return (
          <div>
      <Item></Item>     
        {this.state.json_response.map( ({createdAt,message}) =>
            <div className="Ingbox">
            <ul className="ingredients"><br />
              <h2 className="title">Ingredients:</h2>
          {createdAt.S} {message.S} <br />
      </ul></div>
  )}
    <br />
    <h2>Instructions:</h2>

        
</div>
    );
}

render() {
    return this.renderlist();
}
               
}
